import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useCookies } from "react-cookie";
import playstoreicon from "../images/playstore-icon.png";
import appleicon from "../images/apple-icon.png";
import React from "react";

const AuthModal = ({ setShowModal, isSignUp }) => {
  const [email, setEmail] = useState(null);
  const [password, setPassword] = useState(null);
  const [confirmPassword, setConfirmPassword] = useState(null);
  const [error, setError] = useState(null);
  const [cookies, setCookie, removeCookie] = useCookies(null);
  const [otpGenerated, setOtpGenerated] = useState(false);

  let navigate = useNavigate();

  const handleClick = () => {
    setShowModal(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isSignUp && password !== confirmPassword) {
        setError("Passwords need to match!");
        return;
      }
      // if (isSignUp && password.length < 8) {
      //     setError("Invalid password. Password must contain at least 8 characters, including one letter, one number, and one special character.");
      //     return;
      //   }

      //   if (!/[a-zA-Z]/.test(password) || !/\d/.test(password) || !/[!@#$%^&*()_+]/.test(password)) {
      //     setError("Invalid password. Password must contain at least 8 characters, including one letter, one number, and one special character.");
      //     return;
      //   }

      //   const response = await axios.post(
      //     `http://localhost:8000/verifyUser`, {email, password}
      //   );

      const response = await axios.post(
        `http://localhost:8000/${isSignUp ? "signup" : "login"}`,
        { email, password }
      );

      setCookie("AuthToken", response.data.token);
      setCookie("UserId", response.data.userId);

      const success = response.status === 201;
      if (success && isSignUp) navigate("/onboarding");
      if (success && !isSignUp) navigate("/dashboard");

      window.location.reload();
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="auth-modal">
      <div className="close-icon" onClick={handleClick}>
        â“§
      </div>

      <h2>{isSignUp ? "CREATE ACCOUNT" : "LOG IN"}</h2>
      <p>By clicking Log In, you agree to our terms and conditions.</p>
      {otpGenerated ? (
        <>
          <form onSubmit={handleSubmit}>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="email"
              required={true}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              id="password"
              name="password"
              placeholder="password"
              required={true}
              onChange={(e) => setPassword(e.target.value)}
            />
            {isSignUp && (
              <input
                type="password"
                id="password-check"
                name="password-check"
                placeholder="confirm password"
                required={true}
                onChange={(e) => setConfirmPassword(e.target.value)}
              />
            )}
            <input className="secondary-button" type="submit" />
            <p>{error}</p>
          </form>

          <hr />
          <div className="icon-container">
            <img src={playstoreicon} className="icons"></img>
            <img src={appleicon} className="icons"></img>
          </div>
        </>
      ) : (
        <>
          <form onSubmit={handleSubmit}>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="email"
              required={true}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              id="password"
              name="password"
              placeholder="password"
              required={true}
              onChange={(e) => setPassword(e.target.value)}
            />
            {isSignUp && (
              <input
                type="password"
                id="password-check"
                name="password-check"
                placeholder="confirm password"
                required={true}
                onChange={(e) => setConfirmPassword(e.target.value)}
              />
            )}
            <input
              className="secondary-button"
              type="submit"
              value="Send OTP"
            />
            <p>{error}</p>
          </form>

          <hr />
          <div className="icon-container">
            <img src={playstoreicon} className="icons"></img>
            <img src={appleicon} className="icons"></img>
          </div>
        </>
      )}
    </div>
  );
};
export default AuthModal;
