import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useCookies } from "react-cookie";
import playstoreicon from "../images/playstore-icon.png";
import appleicon from "../images/apple-icon.png";

const AuthModal = ({ setShowModal, isSignUp }) => {
  const [email, setEmail] = useState(null);
  const [password, setPassword] = useState(null);
  const [confirmPassword, setConfirmPassword] = useState(null);
  const [otp, setOtp] = useState(null); // Added OTP state
  const [error, setError] = useState(null);
  const [cookies, setCookie, removeCookie] = useCookies(null);

  let navigate = useNavigate();

  const handleClick = () => {
    setShowModal(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isSignUp && !otp) {
        // If it's a signup and OTP is not entered yet
        const response = await axios.post(`http://localhost:8000/verifyUser`, {
          email,
          password,
        });

        if (response.status === 200) {
          setEmail(email);
          setError(null);
          setOtp(""); // Clear OTP field
        } else {
          setError("Invalid email or user already exists.");
        }
      } else if (isSignUp && otp) {
        // If OTP is entered, treat it as sending OTP
        const response = await axios.post(
          `http://localhost:8000/${isSignUp ? "signup" : "login"}`,
          { email, password, otp }
        );

        if (response.status === 200) {
          navigate("/onboarding");
          window.location.reload();
        } else {
          setError("Invalid OTP. Please try again.");
        }
      } else {
        // For login, use the existing login code
        const response = await axios.post(`http://localhost:8000/login`, {
          email,
          password,
        });

        setCookie("AuthToken", response.data.token);
        setCookie("UserId", response.data.userId);

        const success = response.status === 201;
        if (success && !isSignUp) navigate("/dashboard");

        window.location.reload();
      }
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="auth-modal">
      <div className="close-icon" onClick={handleClick}>
        â“§
      </div>

      <h2>
        {isSignUp && !otp ? "CREATE ACCOUNT" : "LOG IN"}
        {isSignUp && otp && " - Enter OTP"} {/* Show OTP indicator */}
      </h2>
      <p>
        By clicking {isSignUp && !otp ? "Send OTP" : "Log In"}, you agree to our
        terms and conditions.
      </p>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          id="email"
          name="email"
          placeholder="email"
          required={true}
          onChange={(e) => setEmail(e.target.value)}
          disabled={isSignUp && otp} // Disable email field if OTP is entered
        />
        {isSignUp && !otp && (
          <input
            type="password"
            id="password"
            name="password"
            placeholder="password"
            required={true}
            onChange={(e) => setPassword(e.target.value)}
          />
        )}
        {isSignUp && !otp && (
          <input
            type="password"
            id="password-check"
            name="password-check"
            placeholder="confirm password"
            required={true}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        )}
        {isSignUp && otp && (
          <input
            type="text"
            id="otp"
            name="otp"
            placeholder="enter OTP"
            required={true}
            onChange={(e) => setOtp(e.target.value)}
          />
        )}
        <input
          className="secondary-button"
          type="submit"
          value={isSignUp && !otp ? "Send OTP" : "Log In"}
        />
        <p>{error}</p>
      </form>

      <hr />
      <div className="icon-container">
        <img src={playstoreicon} className="icons" alt="Play Store Icon" />
        <img src={appleicon} className="icons" alt="Apple Icon" />
      </div>
    </div>
  );
};
export default AuthModal;
